import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import StatsComponent from './StatsComponent';
import { ReactComponent as MSHFWhiteLogo } from './sports_hall_logo_white.svg';

const WinnerProfilePage = () => {
  const [winner, setWinner] = useState({});
  const navigate = useNavigate();
  const { athleteId } = useParams();

  useEffect(() => {
    fetch(`http://localhost:1337/api/athletes/${athleteId}?populate=*`)
      .then((res) => res.json())
      .then((data) => {
        setWinner(data.data.attributes);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, [athleteId]);

  const [showStats, setShowStats] = useState(false);
  console.log(winner)

  return (
    <div className="container-fluid bg-dark full-bg" style={{ backgroundImage: `url(${winner?.imageLink})`, backgroundSize: 'cover' }}>
      <MSHFWhiteLogo className="img-fluid position-absolute top-0 end-0 whiteLogo" />
      <div className="row justify-content-between">
        <div className="col-md-4">
          <div className="card text-white yearWinnerBanner">
            <div className="card-body">
              <h5 className="card-title text-uppercase profileBanner">{winner.award?.data?.attributes?.name}</h5>
              <img src={winner.award?.data?.attributes?.sponsorLogoLink} alt="sponsor-logo" className="my-4 img-fluid" />
              <p className="card-text">WINNER {winner.years?.data[0]?.attributes?.awardYear}</p>
              <img src={winner.award?.data?.attributes?.iconLink} alt="football-icon" className="bannerIcon" />
            </div>
          </div>
        </div>
      </div>

      <div className="row justify text-white text-uppercase">
        <div className="col-md-4 ">
        <img src={winner.school?.data?.attributes?.logoLink} alt="school-logo" className="my-4" />
          <h3>{winner.firstName} {winner.lastName}</h3>
          <p>{winner.positionFullName}</p>

          <button className="btn  w-100 mb-2 btnStyle profileButton" onClick={() => setShowStats(true)}>Stats</button>
          <button className="btn  w-100 mb-2 btnStyle profileButton" onClick={() => navigate(`/highlights/${athleteId}`)}>Highlights</button>
          <button className="btn btn-primary w-100 mb-2 btnStyle yearBtn" onClick={() => navigate('/awards')}>Menu</button>
        </div>
      </div>

      {showStats && <StatsComponent trophyWinner={winner} onClose={() => setShowStats(false)} />}
    </div>
  );
};

export default WinnerProfilePage;
